"use strict";
function render(element, data) {
    var tpl = document.getElementById(element).innerHTML.replace(/[\r\n\t]/g, '');
    var body = "var p=[]; p.push('"
        + tpl
            .replace(/<%=(.*?)%>/g, "');p.push($1);p.push('")
            .replace(/%>/g, "p.push('")
            .replace(/<%/g, "');")
        + "'); return p.join('');";

    var exec = new Function('data', body);
    return exec(data)
}
